import { useState } from "react"
import { useNavigate } from "react-router-dom"
import supabase from "../config/supabaseClient"

const SignIn = () => {

  const navigate = useNavigate()
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [formError, setFormError] = useState('')

  const handleSignIn = async (e) => {
    e.preventDefault();
  
    if (!email || !password) {
      setFormError('Fill in email and password correctly');
      return;
    }
  
    // Query the User table for the email
    const { data, error } = await supabase
      .from('User')
      .select('Email, Password, Role_ID')
      .eq('Email', email)
      .single();  // get only one row
  
    if (error) {
      console.log(error);
      setFormError('Wrong email or password');
      return;
    }
  
    if (!data) {
      setFormError('Wrong email or password');
      return;
    }
  
    // Check password manually (replace with hashed comparison in real apps)
    if (data.Password !== password) {
      setFormError('Wrong email or password');
      return;
    }
  
    // Successful login — redirect to /Role_ID
    setFormError(null);
    navigate(`/${data.Role_ID}`);
  };
  

  return (
    <div className="signIn">
      <form onSubmit={handleSignIn}>
        <label htmlFor="email">Email:</label>
        <input 
          type="text" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
        />

        <label htmlFor="password">Password:</label>
        <input 
          type="text" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
        />
  
        
  
        <button type="submit">Sign In</button>

        {formError && <p className="error">{ formError }</p>}
      </form>
    </div>
  )
}

export default SignIn
